import { Component, OnInit } from '@angular/core';
import { PdfMakeWrapper, QR, Table, Txt, Img  } from 'pdfmake-wrapper';
import { FileOpener } from '@awesome-cordova-plugins/file-opener/ngx';
import { File } from '@awesome-cordova-plugins/file/ngx';
import { Platform } from '@ionic/angular';
import { UserServiceService } from 'src/app/services/user-service.service';

@Component({
  selector: 'app-certificado',
  templateUrl: './certificado.page.html',
  styleUrls: ['./certificado.page.scss'],
})
export class CertificadoPage implements OnInit {

  public id = localStorage.getItem('id');
  public usuario: any;

  constructor( public file: File,
    public userServives: UserServiceService,
    public fileOpener: FileOpener,
    public platform: Platform) { }

  ngOnInit() {
    this.userServives.getUserDNI(this.id).subscribe(resp => {
      console.log(resp.user);
      
      this.usuario = resp.user;
     
    })
  }


  async imprimir(){

    console.log('object');

    const firmna = 'data:image/jpg;base64,'
    const logo = 'data:image/png;base64,';
    const txt_cert = 'data:image/jpeg;base64,';
    var espacio = '   ';
    const blanco = 'data:image/jpeg;base64,'

    const up_der = 'data:image/png;base64,';
    const down_der = 'data:image/png;base64,';
    const up_izq = 'data:image/png;base64,';
    const down_izq = 'data:image/png;base64,';

    const pdf = new PdfMakeWrapper();
    pdf.pageSize('A4');
    pdf.pageOrientation('landscape');
    pdf.pageMargins([ 10, 10, 10, 10 ]);
    pdf.info({
      title: 'Certificacion ramiro - IE RAMIRO',
      author: 'RAMIRO',
      subject: 'subject of document',
    });

    pdf.defaultStyle({
      fontSize: 7
    });


    pdf.add (new Table([
      [ 
        await new Img(up_der).width('80').alignment('left').build(),
        await new Img(logo).width('250').alignment('center').build(),
        await new Img(up_izq).width('80').alignment('right').build(),
      ],
    ]).alignment('center').italics().layout('noBorders').widths([ 250,305,250  ]).end);
    pdf.add( new Txt(espacio).end);
    pdf.add( new Txt(espacio).end);
    // pdf.add( new Txt(espacio).end);

    // pdf.add( await new Img(logo).width('230').alignment('center').build() );
    // pdf.add( new Txt(espacio).end);
    // pdf.add( new Txt(espacio).end);
    pdf.add( await new Img(txt_cert).width('260').alignment('center').build() );
    pdf.add( new Txt(espacio).end);
    // pdf.add( new Txt(espacio).end);
    pdf.add( new Txt(espacio).end);
    pdf.add( new Txt(`OTORGADO A:`).alignment('center').fontSize(14).color("#07456C").end);
    pdf.add( new Txt(espacio).end);

    pdf.add( new Txt(`${this.usuario.nombre} ${this.usuario.apellidos}`).alignment('center').fontSize(14).color("#07456C").italics().bold().end);
    pdf.add( new Txt(espacio).end);
    // pdf.add( new Txt(espacio).end);
    // pdf.add( new Txt(this.name_cert.toLocaleUpperCase()).alignment('center').fontSize(18).color("#07456C").bold().end);
    pdf.add( new Txt(espacio).end);
    pdf.add( new Txt(espacio).end);
    pdf.add( new Txt(espacio).end);
    // pdf.add( new Txt(espacio).end);
    // pdf.add( new Txt(espacio).end);
    pdf.add( new Txt('Por haber culminado satisfactoriamente capacitaciones').alignment('center').fontSize(12).italics().end);
    pdf.add( new Txt('REPOSITORIOS & HERRAMIENTAS TECNOLÓGICAS').alignment('center').fontSize(16).italics().bold().end);
    pdf.add( new Txt('realizado del 1 al 30 de mayo del 2023 cumpliendo un total').fontSize(12).alignment('center').italics().end);
    pdf.add( new Txt('de 120 horas pedagógicas').alignment('center').fontSize(12).italics().end);
    // pdf.add( new Txt(espacio).end);
    pdf.add( new Txt(espacio).end);
    pdf.add( new Txt(espacio).end);
    pdf.add( new Txt(espacio).end);
    pdf.add( new Txt(espacio).end);


    pdf.add( await new Img(firmna).width('100').alignment('center').build() );


    pdf.add( new Txt(espacio).end);
   
    pdf.add (new Table([
      [ 
        await new Img(down_der).width('80').alignment('left').build(),
        await new Img(blanco).width('190').alignment('center').build(),
        await new Img(down_izq).width('80').alignment('right').build(),
      ],
    ]).alignment('center').italics().layout('noBorders').widths([ 250,305,250  ]).end);

    // pdf.create().download();

    var pdfObject = pdf.create();

    if(this.platform.is('cordova')) {

      pdfObject.getBuffer((buffer) => {
        var blob = new Blob([buffer], {type: 'application/pdf'});
        this.file.writeFile(this.file.dataDirectory,  'micertificado.pdf', blob, { replace: true }).then(fileEntry => {
          this.fileOpener.open(this.file.dataDirectory +  'micertificado.pdf', 'application/pdf');
        });
      });

      return true;
    }
    return true;
  }


}
